#!/usr/bin/env python3# -*- coding: utf-8 -*-from joblib import Parallel, delayedimport numpy as npdef shape(value):    if isinstance(value, np.ndarray):        return value.shape    else:        return Noneif __name__ == '__main__':    results = \        Parallel(n_jobs=-1, verbose=10)(            delayed(shape)(np.zeros(int(i)))                for i in [1e4, 1e5, 1e6])    print("Results: {0}!".format(results))# EOF